mapper.insert:


	SELECT
	       IFNULL(MAX(MESSAGE_CODE) + 1, 1)
	  FROM MESSAGE



	INSERT
	  INTO MESSAGE
	(
	  MESSAGE_CODE
	, MESSAGE_DATE
	, CHAT_ROOM_CODE
	, USER_CODE
	, MESSAGE_CONTENT
	)
    VALUES
    (
      #{messageCode}
    , DATE_FORMAT(now(), '%Y-%m-%d %H:%i:%s')
    , #{roomCode}
    , #{userCode}
    , #{messageContent}
    )


	SELECT
           IFNULL(MAX(CHAT_JOIN_CODE) + 1, 1)
      FROM CHAT_JOIN


INSERT
  INTO CHAT_JOIN
(
  CHAT_JOIN_CODE
, USER_CODE
, CHAT_ROOM_CODE
)
VALUES
(
  #{joinCode}
, #{userCode}
, #{roomCode}
)

mapper.delete:
DELETE
  FROM CHAT_JOIN
WHERE USER_CODE = #{userCode}
  AND CHAT_ROOM_CODE = #{roomCode}
