		  TRIP_INQUIRY_CODE
		, INQUIRY_REASON
		, INQUIRY_YN
		, TRIP_REGIST_LIST_CODE
		, USER_CODE
		)
		VALUES
		(
		  #{inquiryCode}
		, #{inquiryContent}
		, 'N'
		, #{tripRegistCode}
		, #{userCode}
		)
