mapper.insert:
		INSERT INTO USER A
		(
			 A.USER_CODE
		   , A.USER_NAME
		   , A.USER_PHONE
		   , A.USER_PWD
		   , A.USER_BDAY
		   , A.USER_GENDER
		   , A.USER_EMAIL
		   , A.USER_REGISTER_DAY
		)
        VALUES
        (
        	SELECT CASE COUNT(*) WHEN 0 THEN 1 ELSE MAX(SEQ) + 1 END
          , #{ userName }
          , #{ userPhone }
          , #{ userPwd }
          , #{ userBday }
          , #{ userGender }
          , #{ userEmail }
          , SYSDATE()
        )
